import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import {
    Row,
    FormGroup,
    Input,
    Label,
    Button,
    Form,
    Col,
} from 'reactstrap'
import request from "../../helpers/request";

const FormEmployee = ({type, setFormVisible, formEdited, fetchData}) =>{
    
    const [name, setName] = useState('');
    const [quantity, setQuentity] = useState(0);
    const [price, setPrice] = useState(0);

    const handleSubmit = async (e) =>{
        e.preventDefault();

        const form = {name, quantity, price};

        if (type === 'create') {
            await request.post('/employee', form)
                .then(() => fetchData())
                .catch((err) => console.log(err))
        } else {
            await request.put(`/employee/${formEdited.id}`, form)
            .then(() => fetchData())
            .catch((err) => console.log(err))
        }
        setFormVisible(false);
    }

    useEffect(() =>{
        if(type === "edit"){
            setName(formEdited.name);
            setAge(formEdited.age);
        }
    }, [type, formEdited])

    return(
    <>
        <Row>
            <Form onSubmit={handleSubmit}>
            <>
                <FormGroup>
                    <Label>Name</Label>
                    <Input 
                        value={name} 
                        placeholder="Please enter your name" 
                        onChange={(e) => setName(e.target.value)}
                        required 
                    />
                </FormGroup>

                <FormGroup>
                    <Label>Qantity</Label>
                    <Input
                        type = "number"
                        value={quantity}
                        onChange={(e) => setQuentity(e.target.value)}
                        required 
                    />
                </FormGroup>

                <FormGroup>
                    <Label>Price</Label>
                    <Input
                        type = "number"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        required 
                    />
                </FormGroup>

                <Row>
                    <Col>
                        <Button color="success" type="submit">Submit</Button>
                    </Col>
                    <Col>
                    <Button onClick={() => setFormVisible(false)}>Cancel</Button>
                    </Col>
                    
                </Row>
            </>
            </Form>
        </Row>
    </>
    )
}

export default FormEmployee;
